import React, { Fragment, useState, useEffect } from "react";
import { Switch, Route, NavLink, useHistory } from "react-router-dom";
import { Link } from "react-router-dom";
import TheContext from './TheContext';
import Home from "./components/Home"
import AddItems from "./components/AddItems"
import NotFound from "./components/404/NotFound.js";
import SignUp from "./components/auth/SignUp";
import LogIn from "./components/auth/LogIn";
import Profile from "./components/profile/Profile";
import actions from "./api/index";
import GoogleAuth from "./components/auth/GoogleAuth";
import GoogleAuthLogin from "./components/auth/GoogleAuthLogin";
import ShowList from "./components/ShowList"
import { NotificationContainer, NotificationManager } from 'react-notifications';


const App = () => {

  let [user, setUser] = useState(null)
  const [open, setOpen] = useState(false);
console.log(process.env)
  useEffect(() => {
    async function getUser() {
      let user = await actions.getUser();
      console.log('user is', user)
      setUser(user?.data)
    }
    getUser();
  }, [])

  const logOut = async () => {
    let res = await actions.logOut();
    setUser(null);
  };

  const history = useHistory();


  return (
    <TheContext.Provider value={{ history, user, setUser }}>
     {user?.email}
        <img
          src="./GetIt.png"
          alt="our logo"
          id={open ? "clickedlogo" : ""}
          class="logo"
          onClick={() => setOpen(!open)}
        />

        <ul className="hamburgerMenu"  id={open ? "clickedmenu" : ""}>
          <div className="x" onClick={() => setOpen(!open)}>
            X
          </div>
          <Link to="/" style={{ textDecoration: "none" }}>
            <li>Home</li>
          </Link>
          <Link to="/AddAGoal" style={{ textDecoration: "none" }}>
            <li>Add a Item</li>
          </Link>

        </ul>

      {/* <nav>
        <NavLink to="/">Home</NavLink>

        {user ? (
          <Fragment>
            <NavLink onClick={logOut} to="/">
              Log Out
            </NavLink>
            <NavLink to="/profile">Profile</NavLink>
          </Fragment>
        ) : (
            <Fragment>
              <NavLink to="/sign-up">Sign Up</NavLink>
              <NavLink to="/log-in">Log In</NavLink>
            </Fragment>
          )}
      </nav> */}
      <Switch>
        <Route exact path="/" render={(props) => <Home {...props} />} />
        <Route
          exact
          path="/sign-up"
          render={(props) => <SignUp {...props} setUser={setUser} />}
        />
        <Route
          exact
          path="/log-in"
          render={(props) => <LogIn {...props} setUser={setUser} />}
        />
         <Route
          exact
          path="/see-list"
          render={(props) =><ShowList {...props} />}
        />
        <Route
          exact
          path="/profile"
          render={(props) => <Profile {...props} />}
        />
<Route
          exact
          path="/list/:listid"
          render={(props) => <AddItems {...props} />}
        />
        <Route component={NotFound} />
      </Switch>
      {!user && <GoogleAuth setUser={setUser} />}
      {!user && <GoogleAuthLogin setUser={setUser} />}

      <NotificationContainer />

    </TheContext.Provider>

  )

}
export default App;
